import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const SplashPage = () => {
  const navigate = useNavigate();
  const [jumpIn, setJumpIn] = useState(false);

  useEffect(() => {
    // Set jumpIn state to true after a short delay to trigger the animation
    const timer = setTimeout(() => {
      setJumpIn(true); // Start jump effect
    }, 100);

    // Navigate to the signup page after 3 seconds
    setTimeout(() => {
      navigate('/LandingPage');
    }, 3000);

    // Cleanup the timer on component unmount
    return () => {
      clearTimeout(timer);
    };
  }, [navigate]);

  const splashContainerStyle = {
    backgroundColor: '#00183d', // Background color
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    overflow: 'hidden',
  };

  const logosStyle = {
    width: '300px', // Increase the logo size
    height: 'auto',
    opacity: jumpIn ? 1 : 0, // Fade-in effect
    transform: jumpIn ? 'translateY(0) scale(1)' : 'translateY(-100vh) scale(0)', // Jump-in effect
    animation: jumpIn ? 'bounce 2s ease-out' : 'none', // Slower bounce effect
    transition: 'opacity 1.5s ease, transform 2s ease', // Smooth and slower transition
  };

  // Injecting keyframes dynamically into the document head
  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      @keyframes bounce {
        0% {
          transform: translateY(-100vh) scale(0);
        }
        30% {
          transform: translateY(40px) scale(1.2);
        }
        50% {
          transform: translateY(-20px) scale(1.1);
        }
        70% {
          transform: translateY(10px) scale(1.05);
        }
        100% {
          transform: translateY(0) scale(1);
        }
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style); // Clean up style when component unmounts
  }, []);

  return (
    <div style={splashContainerStyle}>
      <img
        src={require('./../../assets/logo.png')}
        alt="logos"
        style={logosStyle}
      />
    </div>
  );
};

export default SplashPage;
